DROP TABLE IF EXISTS `plantacao`;
CREATE TABLE IF NOT EXISTS `plantacao` (
  `id_plantacao` int(11) NOT NULL AUTO_INCREMENT,
  `reino` varchar(10) NOT NULL,
  `divisao` varchar(15) NOT NULL,
  `classe` varchar(15) NOT NULL,
  `ordem` varchar(15) NOT NULL,
  `familia` varchar(20) NOT NULL,
  `genero` varchar(20) NOT NULL,
  `nome` varchar(30) NOT NULL,
  `temperatura` varchar(30) NOT NULL,
  `agua` varchar(100) NOT NULL,
  `solo` varchar(250) NOT NULL,
  `descricao` varchar(5000) NOT NULL,
  `foto` varchar(30) NOT NULL,
  PRIMARY KEY (`id_plantacao`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=latin1;

